import Vue from "vue";
import Vuex from "vuex";
import teamService from "@/services/team.service";
import heroService from "@/services/hero.service";
import orgService from "@/services/org.service";

Vue.config.devtools = true;
Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    orgPassword: "",
    heroesAliases: [],
    currentHero: null,
    teams: [],
    currentTeam: null,
    orgNames: [],
    currentOrg: null,
  },
  mutations: {
    SET_ORG_PASSWORD(state, password) {
      state.orgPassword = password;
    },
    SET_HEROES_ALIASES(state, aliases) {
      state.heroesAliases = aliases;
    },
    SET_CURRENT_HERO(state, hero) {
      state.currentHero = hero;
    },
    SET_TEAMS(state, teams) {
      state.teams = teams;
    },
    SET_CURRENT_TEAM(state, team) {
      state.currentTeam = team;
    },
    SET_ORG_NAMES(state, names) {
      state.orgNames = names;
    },
    SET_CURRENT_ORG(state, org) {
      state.currentOrg = org;
    },
  },
  actions: {
    updateOrgPassword({ commit }, password) {
      commit("SET_ORG_PASSWORD", password);
    },
    updateHeroesAliases({ commit }, aliases) {
      commit("SET_HEROES_ALIASES", aliases);
    },
    setCurrentHero({ commit }, hero) {
      commit("SET_CURRENT_HERO", hero);
    },
    updateTeams({ commit }, teams) {
      commit("SET_TEAMS", teams);
    },
    setCurrentTeam({ commit }, team) {
      commit("SET_CURRENT_TEAM", team);
    },
    updateOrgNames({ commit }, names) {
      commit("SET_ORG_NAMES", names);
    },
    setCurrentOrg({ commit }, org) {
      commit("SET_CURRENT_ORG", org);
    },

    // Heroes
    fetchHeroes({ commit }) {
      heroService.getHeroes().then((aliases) => {
        commit("SET_HEROES_ALIASES", aliases);
      });
    },

    async createHero({ dispatch }, { publicName, realName, powers }) {
      const response=await heroService.createHero(publicName, realName, powers);
      dispatch("fetchHeroes");
      return response;
    },

    updateHero({ dispatch }, { id, publicName, realName, powers }) {
      heroService.updateHero(id, publicName, realName, powers).then(() => {
        dispatch("fetchHeroes");
      });
    },

    async fetchHeroById({ commit }, id) {
      const response=await heroService.getHeroById("nous sommes mechants",id);
      commit("SET_CURRENT_HERO", response);
      return response;
    },

    //Teams
    fetchTeams({ commit }) {
      teamService.getTeams().then((teams) => {
        commit("SET_TEAMS", teams);
      });
    },

    createTeam({ dispatch }, teamName) {
      teamService.createTeam(teamName.name).then(() => {
        dispatch("fetchTeams");
      });
    },

    updateTeamAddHeroes({ dispatch }, { teamId, addheroes }) {
      teamService.updateTeamAddHeroes(teamId, addheroes).then(() => {
        dispatch("fetchTeams");
      });
    },

    updateTeamRemoveHeroes({ dispatch }, { teamId, removeHeroes }) {
      teamService.updateTeamRemoveHeroes(teamId, removeHeroes).then(() => {
        dispatch("fetchTeams");
      });
    },

    //Orgs
    fetchOrgs({ commit }) {
      orgService.getOrg().then((organizations) => {
        commit("SET_ORG_NAMES", organizations);
      });
    },

    createOrg({ dispatch }, { name,password }) {
      orgService.createOrg(name, password).then(() => {
        dispatch('updateOrgPassword',password)
        dispatch("fetchOrgs");
      });
    },

    updateOrgAddTeam({ dispatch }, { idTeam }) {
      orgService
        .updateOrgAddTeam(this.state.orgPassword.password, idTeam)
        .then(() => {
          dispatch("fetchOrgs");
        });
    },

    updateOrgRemoveTeam({ dispatch }, { idTeam }) {
      orgService
        .updateOrgRemoveTeam(this.state.orgPassword.password, idTeam)
        .then(() => {
          dispatch("fetchOrgs");
        });
    },

    fetchOrgById({ commit }, id) {
      orgService
        .getOrgById("nous sommes mechants", id.id)
        .then((organization) => {
          commit("SET_CURRENT_ORG", organization);
        });
    },
  },
});
