import axiosService from "./axios.service";
export default {
  getOrg() {
    return axiosService.get("/orgs/get");
  },
  createOrg(name, secret) {
    return axiosService.post("/orgs/create", {
      name: name,
      secret: secret,
    });
  },
  updateOrgAddTeam(orgSercret, idTeam) {
    return axiosService.put(`/orgs/addteam?org-secret=${orgSercret}`, {
      idTeam: idTeam,
    });
  },
  updateOrgRemoveTeam(teamId, addheroes) {
    return axiosService.put(`/orgs/removeteam?org-secret=${orgSercret}`, {
      idTeam: idTeam,
    });
  },

  getOrgById(id) {
    return axiosService.get(`/orgs/getbyid/${id}`);
  },
};
